package week1.day2;

public class LearnIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int mark = 360;
		
		if(mark>400)
		{
			System.out.println("Mark is greater than 400");
		
		}
		else if(mark>320)
		{
			System.out.println("Mark is greater than 320");
		}
		else
		{
			System.out.println("Less than 360");
		}
	}

}
